import React, { useState } from "react";
import { Button, Col, Container, Row, Table } from "react-bootstrap";
// import { useHistory } from "react-router-dom";
import MainScreen from "../../Components/MainScreen";
import Loading from "../../Components/Loading";
import ErrorMessage from "../../Components/ErrorMessage";
import SuccessMessage from "../../Components/SuccessMessage";
// import axios from "axios";

const PaymentPage = () => {
  //   const history = useHistory();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const [success, setSuccess] = useState(false);
  const [data, setData] = useState(JSON.parse(localStorage.getItem("student")));
  const [fname, setFname] = useState(data.fname);
  const [lname, setLname] = useState(data.lname);
  const [email, setEmail] = useState(data.email);
  const [phone, setPhone] = useState(data.phone);

  const loadScript = (src) => {
    return new Promise((resolve) => {
      const script = document.createElement("script");
      script.src = src;
      script.onload = () => {
        resolve(true);
      };
      script.onerror = () => {
        resolve(false);
      };
      document.body.appendChild(script);
    });
  };

  const displayRazorpay = async (e) => {
    e.preventDefault();
    const res = await loadScript(
      "https://checkout.razorpay.com/v1/checkout.js"
    );
    if (!res) {
      setError("Failed Loading Razorpay");
    } else {
      const options = {
        key: process.env.RAZORPAY_KEY, // Enter the Key ID generated from the Dashboard
        amount: "50000", // Amount is in currency subunits. Default currency is INR. Hence, 50000 refers to 50000 paise
        currency: "INR",
        name: "Walchand Collge of Engineering, Sangli",
        description: "Spot Round Fee Payment",
        image: "https://img.collegepravesh.com/2018/11/WCE-Sangli-Logo.png",
        order_id: "order_DBJOWzybf0sJbb", //This is a sample Order ID. Pass the `id` obtained in the previous step
        handler: function (response) {
          alert(response.razorpay_payment_id);
        },
        prefill: {
          name: `${fname} ${lname}`,
          email: email,
          contact: `91${phone}`,
        },
        notes: {
          address: "Razorpay Corporate Office",
        },
        theme: {
          color: "#3399cc",
        },
      };
      const rzp = new window.Razorpay(options);
      rzp.open();
      e.preventDefault();
    }
  };
  return (
    <MainScreen title={"Spot Round Fee Payment"}>
      {error && <ErrorMessage variant="danger">{error}</ErrorMessage>}
      {success && <SuccessMessage variant="success">{success}</SuccessMessage>}
      {loading && <Loading></Loading>}
      <Container fluid>
        <Row>
          <Table bordered>
            <tbody>
              <tr>
                <td style={{ width: "50vw" }} colSpan={2}>
                  <b> Payment Details</b>
                </td>
              </tr>
              <tr>
                <td style={{ width: "50vw" }}>Name</td>
                <td style={{ width: "50vw" }}>
                  {fname} {lname}
                </td>
              </tr>
              <tr>
                <td>Email</td>
                <td>{email}</td>
              </tr>
              <tr>
                <td>Mobile Number</td>
                <td>{phone}</td>
              </tr>
              <tr>
                <td>Amount To Pay</td>
                <td>1000 Rs</td>
              </tr>
            </tbody>
          </Table>
        </Row>
        <Row>
          <Button
            type="button"
            onClick={(e) => {
              displayRazorpay(e);
            }}
          >
            Proceed
          </Button>
        </Row>
      </Container>
    </MainScreen>
  );
};

export default PaymentPage;
